mapper.select:
SELECT * FROM ( SELECT ROWNUM R_NUM, C.* FROM (select p.*, i.price, i.discount, i.reg_date from product p, item i
where p.product_id=i.product_id and product_Category_Num=#{product_category_num}
order by reg_date desc )C )
order by price desc ) C )
order by price asc ) C )
WHERE R_NUM BETWEEN #{begin} and #{end}

SELECT COUNT(*)
FROM PRODUCT
WHERE PRODUCT_CATEGORY_NUM = #{product_category_num}

select decode(count(*),1, 'true', 0, 'false') 
from product 
where product_id = #{product_id}

select decode(count(*),1, 'true', 0, 'false') 
from item 
where item_num = #{item_num}
